void InitTIMER0(void)// Timer0 initialize to tick every 1ms
{
	/* Step 1. Enable and Select Timer clock source */          
	SYSCLK->CLKSEL1.TMR0_S = 0;	// Select 12Mhz for Timer0 clock source 
	SYSCLK->APBCLK.TMR0_EN =1;	// Enable Timer0 clock source

	/* Step 2. Select Operation mode */	
	TIMER0->TCSR.MODE = PERIODIC;		// Select once mode for operation mode

	/* Step 3. Select Time out period = (Period of timer clock input) * (8-bit Prescale + 1) * (24-bit TCMP)*/
	TIMER0->TCSR.PRESCALE = 11;	// Set Prescale [0~255]
	TIMER0->TCMPR = 1000;				// Set TCMPR [0~16777215]
	//Timeout period = (1 / 12MHz) * ( 11 + 1 ) * 1,000 = 1 ms

	/* Step 4. Enable interrupt */
	TIMER0->TCSR.IE = 1;				// Enable Timer0 Interrupt
	TIMER0->TISR.TIF = 1;				// Write 1 to clear for safty		
	NVIC_EnableIRQ(TMR0_IRQn);	// Enable Timer0 Interrupt function 

	/* Step 5. Enable Timer module */
	TIMER0->TCSR.CRST = 1;	// Reset up counter
	TIMER0->TCSR.CEN = 0;		// disable Timer0
}
void InitINT_GPIO(void)
{
	// setup to get interrupt input
	DrvGPIO_Open(E_GPA, 0, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 1, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 2, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 3, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 4, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 5, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 6, E_IO_INPUT);
	DrvGPIO_Open(E_GPA, 9, E_IO_INPUT);
	//Set the specified GPIO pin to the specified GPIO Interrupt mode
  DrvGPIO_EnableInt(E_GPA, 0, E_IO_RISING,  E_MODE_EDGE);
  DrvGPIO_EnableInt(E_GPA, 1, E_IO_FALLING, E_MODE_EDGE);
	DrvGPIO_EnableInt(E_GPA, 2, E_IO_RISING,  E_MODE_EDGE);
  DrvGPIO_EnableInt(E_GPA, 3, E_IO_FALLING, E_MODE_EDGE);
	DrvGPIO_EnableInt(E_GPA, 4, E_IO_RISING,  E_MODE_EDGE);
  DrvGPIO_EnableInt(E_GPA, 5, E_IO_FALLING, E_MODE_EDGE);
	DrvGPIO_EnableInt(E_GPA, 6, E_IO_RISING,  E_MODE_EDGE);
  DrvGPIO_EnableInt(E_GPA, 9, E_IO_FALLING, E_MODE_EDGE);
	//Enable the de-bounce function
  DrvGPIO_SetDebounceTime(5, 1);
	DrvGPIO_EnableDebounce(E_GPA, 0);
	DrvGPIO_EnableDebounce(E_GPA, 1);	
	DrvGPIO_EnableDebounce(E_GPA, 2);
	DrvGPIO_EnableDebounce(E_GPA, 3);	
	DrvGPIO_EnableDebounce(E_GPA, 4);
	DrvGPIO_EnableDebounce(E_GPA, 5);	
	DrvGPIO_EnableDebounce(E_GPA, 6);
	DrvGPIO_EnableDebounce(E_GPA, 9);	
}
void InitUART(void)
{
	STR_UART_T sParam;
	
	DrvGPIO_InitFunction(E_FUNC_UART0); // Set UART pins
	
	//* UART Setting */
	sParam.u32BaudRate 	= 9600;
	sParam.u8cDataBits 	= DRVUART_DATABITS_8;
	sParam.u8cStopBits 	= DRVUART_STOPBITS_1;
	sParam.u8cParity 	= DRVUART_PARITY_NONE;
	sParam.u8cRxTriggerLevel= DRVUART_FIFO_1BYTES;
	
	// 	/* Set UART Configuration */
	if(DrvUART_Open(UART_PORT0,&sParam) != E_SUCCESS);
}
